import React from 'react'
import './Message.css'

const Message = ({ user,message,classs }) => {
  if(user){
    return (
      <>
      <div className='messageBox right'>{message}</div>
      </>
    )
  }
  else{
  return (
    <>
    <div className='messageBox left'>{message}</div>
    </>
  )
}
}

export default Message
